import React, { useState } from 'react';
import './Food.css';

function Food() {
  const [selectedQuantities, setSelectedQuantities] = useState({});

  const products = [
    {
      id: 1,
      name: 'Hot Dog',
      description: 'Classic all-beef hot dog',
      price: 4.99,
      image: '/food/food-hot-dog.png',
      inStock: 100
    },
    {
      id: 2,
      name: 'Hamburger',
      description: 'Fresh grilled 1/3 lb beef patty',
      price: 6.99,
      image: '/food/food-hamburger.png',
      inStock: 80
    },
    {
      id: 3,
      name: 'Club Sandwich',
      description: 'Triple-decker with turkey, bacon, and lettuce',
      price: 8.99,
      image: '/food/food-club-sandwich.png',
      inStock: 50
    },
    {
      id: 4,
      name: 'Chips',
      description: 'Assorted flavors of potato chips',
      price: 1.99,
      image: '/food/food-chips.png',
      inStock: 150
    },
    {
      id: 5,
      name: 'Soft Drink',
      description: 'Various sodas and beverages',
      price: 2.49,
      image: '/food/food-soft-drink.png',
      inStock: 200
    },
    {
      id: 6,
      name: 'Beer',
      description: 'Domestic and imported options',
      price: 4.99,
      image: '/food/food-beer.png',
      inStock: 120
    },
    {
      id: 7,
      name: 'Bottled Water',
      description: 'Ice-cold purified water',
      price: 1.99,
      image: '/food/drink-water.png',
      inStock: 200
    },
    {
      id: 8,
      name: 'Candy Bar',
      description: 'Various chocolate bars',
      price: 1.99,
      image: '/food/food-candy-bar.png',
      inStock: 150
    }
  ];

  const handleQuantityChange = (productId, quantity) => {
    setSelectedQuantities(prev => ({
      ...prev,
      [productId]: quantity
    }));
  };

  return (
    <div className="food-page">
      <h1>Food & Drinks</h1>
      <p className="subtitle">Order food and drinks for delivery on the course</p>

      <div className="food-grid">
        {products.map(product => (
          <div key={product.id} className="food-card">
            <img 
              src={product.image} 
              alt={product.name} 
              className="food-image"
            />
            <h3>{product.name}</h3>
            <p className="description">{product.description}</p>
            <p className="price">${product.price.toFixed(2)}</p>
            <p className="stock">In stock: {product.inStock}</p>
            
            <select
              value={selectedQuantities[product.id] || 0}
              onChange={(e) => handleQuantityChange(product.id, parseInt(e.target.value))}
              className="quantity-select"
            >
              {[...Array(11)].map((_, i) => (
                <option key={i} value={i}>{i}</option>
              ))}
            </select>
          </div>
        ))}
      </div>

      <div className="delivery-options">
        <button className="delivery-button">Course Delivery</button>
        <button className="delivery-button pickup">Pickup at Turn</button>
      </div>
    </div>
  );
}

export default Food;
