import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './OrderDetails.css';

function OrderDetails() {
  const routeLocation = useLocation();
  const navigate = useNavigate();
  const { cart = [], subtotal = 0 } = routeLocation.state || {};
  
  const [tip, setTip] = useState('');
  const [userLocation, setUserLocation] = useState('');
  const [wearing, setWearing] = useState('');
  const [instructions, setInstructions] = useState('');
  const [errors, setErrors] = useState({});
  
  const TAX_RATE = 0.086; // 8.6%
  const DELIVERY_FEE = 2.00;
  
  const tax = subtotal * TAX_RATE;
  const total = subtotal + tax + DELIVERY_FEE + (parseFloat(tip) || 0);

  const validateForm = () => {
    const newErrors = {};
    
    if (!userLocation.trim()) {
      newErrors.location = 'Location is required';
    }
    
    if (!wearing.trim()) {
      newErrors.wearing = 'Please tell us what you\'re wearing';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      navigate('/order-confirmation');
    }
  };

  return (
    <div className="order-details">
      <h1>Food and Drink Order</h1>
      <p>Give us the details of where to find you, and we'll make sure you get your order asap.</p>

      <div className="order-summary">
        <h3>Your Order:</h3>
        {cart.map((item) => (
          <div key={item.id} className="order-item">
            <span className="item-name">{item.name}</span>
            <span className="item-price">${item.price.toFixed(2)}</span>
          </div>
        ))}

        <div className="order-calculations">
          <div className="subtotal">
            <span>Subtotal</span>
            <span>${subtotal.toFixed(2)}</span>
          </div>
          <div className="tax">
            <span>Sales Tax (8.6%)</span>
            <span>${tax.toFixed(2)}</span>
          </div>
          <div className="delivery-fee">
            <span>Delivery Fee</span>
            <span>${DELIVERY_FEE.toFixed(2)}</span>
          </div>
          <div className="tip-input">
            <span>Tip:</span>
            <input
              type="number"
              placeholder="Enter tip amount"
              value={tip}
              onChange={(e) => setTip(e.target.value)}
            />
          </div>
          <div className="total">
            <span>Total</span>
            <span>${total.toFixed(2)}</span>
          </div>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="delivery-form">
        <div className="form-group">
          <label>Your Location:*</label>
          <div className="location-input">
            <input
              type="text"
              placeholder="e.g., Hole 7, near the bunker"
              value={userLocation}
              onChange={(e) => setUserLocation(e.target.value)}
              className={errors.location ? 'error' : ''}
            />
            <button type="button" className="share-location">
              üìçShare my location
            </button>
          </div>
          {errors.location && <span className="error-message">{errors.location}</span>}
        </div>

        <div className="form-group">
          <label>What You're Wearing:*</label>
          <input
            type="text"
            placeholder="e.g., Red shirt, khaki shorts"
            value={wearing}
            onChange={(e) => setWearing(e.target.value)}
            className={errors.wearing ? 'error' : ''}
          />
          {errors.wearing && <span className="error-message">{errors.wearing}</span>}
        </div>

        <div className="form-group">
          <label>Additional Instructions:</label>
          <textarea
            placeholder="Any special instructions for delivery..."
            value={instructions}
            onChange={(e) => setInstructions(e.target.value)}
          />
        </div>

        <div className="payment-method">
          <span>Payment Method: Visa ending in 1234</span>
          <button type="button" className="edit-payment">Edit</button>
        </div>

        <button type="submit" className="place-order-button">
          Place Order
        </button>
      </form>
    </div>
  );
}

export default OrderDetails;
