import React, { useState, useEffect } from 'react';
import MenuItem from '../components/food/MenuItem';
import './Food.css';
import { useNavigate } from 'react-router-dom';

function Food() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const defaultItems = [
      {
        id: 2,
        name: "Club Sandwich",
        description: "Triple-decker with turkey, bacon, lettuce, and tomato",
        price: 9.99,
        quantity: 1,
        image: "/food/food-clubsandwich.png"
      },
      {
        id: 11,
        name: "Iced Tea",
        description: "Freshly brewed and chilled",
        price: 2.99,
        quantity: 1,
        image: "/food/drink-icedtea.png"
      },
      {
        id: 12,
        name: "Water",
        description: "Bottled spring water",
        price: 1.99,
        quantity: 1,
        image: "/food/drink-water.png"
      }
    ];

    setCart(defaultItems);
  }, []);

  const handleAddToCart = (item, quantity) => {
    if (quantity === 0) {
      setCart(prevCart => prevCart.filter(cartItem => cartItem.id !== item.id));
      return;
    }

    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => cartItem.id === item.id);
      if (existingItem) {
        return prevCart.map(cartItem =>
          cartItem.id === item.id
            ? { ...cartItem, quantity }
            : cartItem
        );
      }
      return [...prevCart, { ...item, quantity }];
    });
  };

  const calculateSubtotal = () => {
    return cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  };

  const handleDeliveryClick = () => {
    if (cart.length > 0) {
      navigate('/order-details', { 
        state: { 
          cart: cart,
          subtotal: calculateSubtotal()
        }
      });
    } else {
      alert('Please add items to your cart first');
    }
  };

  const getItemQuantity = (itemId) => {
    const cartItem = cart.find(item => item.id === itemId);
    return cartItem ? cartItem.quantity : 0;
  };

  const foodItems = [
    {
      id: 1,
      name: "Hot Dog",
      description: "Classic all-beef hot dog with your choice of toppings",
      price: 5.99,
      image: "/food/food-hot-dog.png"
    },
    {
      id: 2,
      name: "Club Sandwich",
      description: "Triple-decker with turkey, bacon, lettuce, and tomato",
      price: 9.99,
      image: "/food/food-club-sandwich.png"
    },
    {
      id: 3,
      name: "Chicken Wings",
      description: "6 crispy wings with your choice of sauce",
      price: 8.99,
      image: "/food/food-chicken-wings.png"
    },
    {
      id: 4,
      name: "Caesar Salad",
      description: "Crisp romaine, croutons, parmesan, and Caesar dressing",
      price: 7.99,
      image: "/food/food-caesar-salad.png"
    },
    {
      id: 5,
      name: "Burger",
      description: "Quarter-pound beef patty with lettuce, tomato, and cheese",
      price: 10.99,
      image: "/food/food-burger.png"
    },
    {
      id: 6,
      name: "Grilled Chicken Wrap",
      description: "Grilled chicken, lettuce, tomato, and ranch in a wrap",
      price: 8.99,
      image: "/food/food-grilled-chicken-wrap.png"
    },
    {
      id: 7,
      name: "Nachos",
      description: "Tortilla chips topped with cheese, jalapeños, and salsa",
      price: 7.99,
      image: "/food/food-nachos.png"
    }
  ];

  const drinks = [
    {
      id: 8,
      name: "Beer",
      description: "Domestic draft beer",
      price: 4.99,
      image: "/food/drink-beer.png"
    },
    {
      id: 9,
      name: "Gatorade",
      description: "Electrolyte-packed sports drink",
      price: 3.99,
      image: "/food/drink-gatorade.png"
    },
    {
      id: 10,
      name: "Soda",
      description: "Your choice of carbonated soft drink",
      price: 2.99,
      image: "/food/drink-soda.png"
    },
    {
      id: 11,
      name: "Iced Tea",
      description: "Freshly brewed and chilled",
      price: 2.99,
      image: "/food/drink-iced-tea.png"
    },
    {
      id: 12,
      name: "Water",
      description: "Bottled spring water",
      price: 1.99,
      image: "/food/drink-water.png"
    }
  ];

  return (
    <div className="food-page">
      <div className="food-header">
        <h1>Eats and Drinks</h1>
        <p>Place your order for pickup or delivery to you on the course!</p>
      </div>

      <section className="menu-section">
        <h2>Food</h2>
        <div className="menu-grid">
          {foodItems.map((item) => (
            <MenuItem 
              key={item.id}
              item={item}
              onAddToCart={(quantity) => handleAddToCart(item, quantity)}
              defaultQuantity={getItemQuantity(item.id)}
            />
          ))}
        </div>
      </section>

      <section className="menu-section">
        <h2>Drinks</h2>
        <div className="menu-grid">
          {drinks.map((item) => (
            <MenuItem 
              key={item.id}
              item={item}
              onAddToCart={(quantity) => handleAddToCart(item, quantity)}
              defaultQuantity={getItemQuantity(item.id)}
            />
          ))}
        </div>
      </section>

      <div className="delivery-options">
        <button 
          className="delivery-button" 
          onClick={handleDeliveryClick}
        >
          Course Delivery
        </button>
        <button className="pickup-button">Pickup</button>
      </div>
    </div>
  );
}

export default Food;
