import React, { useState } from 'react';
import ProductGrid from '../components/ProductGrid';

function Food() {
  const [selectedQuantities, setSelectedQuantities] = useState({});
  const [deliveryType, setDeliveryType] = useState('');

  const products = [
    {
      id: 1,
      name: 'Hot Dog',
      description: 'Classic all-beef hot dog',
      price: 4.99,
      image: '/food/hot-dog.jpg',
      inStock: 100
    },
    {
      id: 2,
      name: 'Hamburger',
      description: 'Fresh grilled 1/3 lb beef patty',
      price: 6.99,
      image: '/food/hamburger.jpg',
      inStock: 80
    },
    {
      id: 3,
      name: 'Club Sandwich',
      description: 'Triple-decker with turkey, bacon, and lettuce',
      price: 8.99,
      image: '/food/club-sandwich.jpg',
      inStock: 50
    },
    {
      id: 4,
      name: 'Chips',
      description: 'Assorted flavors of potato chips',
      price: 1.99,
      image: '/food/chips.jpg',
      inStock: 150
    },
    {
      id: 5,
      name: 'Soft Drink',
      description: 'Various sodas and beverages',
      price: 2.49,
      image: '/food/soft-drink.jpg',
      inStock: 200
    },
    {
      id: 6,
      name: 'Beer',
      description: 'Domestic and imported options',
      price: 4.99,
      image: '/food/beer.jpg',
      inStock: 120
    },
    {
      id: 7,
      name: 'Bottled Water',
      description: 'Ice-cold purified water',
      price: 1.99,
      image: '/food/water.jpg',
      inStock: 200
    },
    {
      id: 8,
      name: 'Candy Bar',
      description: 'Various chocolate bars',
      price: 1.99,
      image: '/food/candy.jpg',
      inStock: 150
    }
  ];

  const handleQuantityChange = (productId, quantity) => {
    setSelectedQuantities(prev => ({
      ...prev,
      [productId]: quantity
    }));
  };

  const handleDeliveryOption = (type) => {
    setDeliveryType(type);
    // Add any additional delivery logic here
    console.log(`Selected delivery option: ${type}`);
    
    // Example: Process the order with selected quantities and delivery type
    const order = {
      items: selectedQuantities,
      deliveryType: type
    };
    console.log('Processing order:', order);
  };

  const deliveryOptions = [
    {
      id: 'delivery',
      label: 'Course Delivery',
      onClick: () => handleDeliveryOption('delivery')
    },
    {
      id: 'pickup',
      label: 'Pickup at Turn',
      onClick: () => handleDeliveryOption('pickup')
    }
  ];

  return (
    <ProductGrid
      title="Food & Drinks"
      subtitle="Order food and drinks for delivery on the course"
      products={products}
      onQuantityChange={handleQuantityChange}
      selectedQuantities={selectedQuantities}
      deliveryOptions={deliveryOptions}
    />
  );
}

export default Food;
