import React, { useState } from 'react';
import MenuItem from '../components/food/MenuItem';
import './Food.css';

function Food() {
  const [cart, setCart] = useState([]);

  const handleAddToCart = (item, quantity) => {
    if (quantity === 0) {
      setCart(prevCart => prevCart.filter(cartItem => cartItem.id !== item.id));
      return;
    }

    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => cartItem.id === item.id);
      if (existingItem) {
        return prevCart.map(cartItem =>
          cartItem.id === item.id
            ? { ...cartItem, quantity }
            : cartItem
        );
      }
      return [...prevCart, { ...item, quantity }];
    });
  };

  const foodItems = [
    {
      id: 1,
      name: "Hot Dog",
      description: "Classic all-beef hot dog with your choice of toppings",
      price: 5.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 2,
      name: "Club Sandwich",
      description: "Triple-decker with turkey, bacon, lettuce, and tomato",
      price: 9.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 3,
      name: "Chicken Wings",
      description: "6 crispy wings with your choice of sauce",
      price: 8.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 4,
      name: "Caesar Salad",
      description: "Crisp romaine, croutons, parmesan, and Caesar dressing",
      price: 7.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 5,
      name: "Burger",
      description: "Quarter-pound beef patty with lettuce, tomato, and cheese",
      price: 10.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 6,
      name: "Grilled Chicken Wrap",
      description: "Grilled chicken, lettuce, tomato, and ranch in a wrap",
      price: 8.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 7,
      name: "Nachos",
      description: "Tortilla chips topped with cheese, jalapeños, and salsa",
      price: 7.99,
      image: "https://placehold.co/400x300"
    }
  ];

  const drinks = [
    {
      id: 8,
      name: "Beer",
      description: "Domestic draft beer",
      price: 4.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 9,
      name: "Gatorade",
      description: "Electrolyte-packed sports drink",
      price: 3.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 10,
      name: "Soda",
      description: "Your choice of carbonated soft drink",
      price: 2.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 11,
      name: "Iced Tea",
      description: "Freshly brewed and chilled",
      price: 2.99,
      image: "https://placehold.co/400x300"
    },
    {
      id: 12,
      name: "Water",
      description: "Bottled spring water",
      price: 1.99,
      image: "https://placehold.co/400x300"
    }
  ];

  return (
    <div className="food-page">
      <div className="food-header">
        <h1>Eats and Drinks</h1>
        <p>Place your order for pickup or delivery to you on the course!</p>
      </div>

      <section className="menu-section">
        <h2>Food</h2>
        <div className="menu-grid">
          {foodItems.map((item) => (
            <MenuItem 
              key={item.id}
              item={item}
              onAddToCart={(quantity) => handleAddToCart(item, quantity)}
            />
          ))}
        </div>
      </section>

      <section className="menu-section">
        <h2>Drinks</h2>
        <div className="menu-grid">
          {drinks.map((item) => (
            <MenuItem 
              key={item.id}
              item={item}
              onAddToCart={(quantity) => handleAddToCart(item, quantity)}
            />
          ))}
        </div>
      </section>

      <div className="delivery-options">
        <button className="delivery-button">Course Delivery</button>
        <button className="pickup-button">Pickup</button>
      </div>
    </div>
  );
}

export default Food;
