import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './OrderDetails.css';

function OrderDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Check if we have cart data and specifically the items array
  const cartItems = location.state?.cart?.items || [];
  const deliveryMethod = location.state?.cart?.deliveryMethod || 'course';

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.total, 0);
  };

  const handleBack = () => {
    navigate(-1);
  };

  // If no cart items, show a message
  if (cartItems.length === 0) {
    return (
      <div className="order-details-page">
        <h1>No Items in Cart</h1>
        <button onClick={handleBack} className="back-button">
          Return to Menu
        </button>
      </div>
    );
  }

  return (
    <div className="order-details-page">
      <h1>Order Details</h1>
      <p className="delivery-method">
        Delivery Method: {deliveryMethod === 'course' ? 'Course Delivery' : 'Pickup'}
      </p>

      <div className="order-items">
        {cartItems.map((item, index) => (
          <div key={index} className="order-item">
            <div className="item-info">
              <span className="item-name">{item.name}</span>
              <span className="item-quantity">x{item.quantity}</span>
            </div>
            <div className="item-price">
              ${(item.total).toFixed(2)}
            </div>
          </div>
        ))}
      </div>

      <div className="order-total">
        <span>Total:</span>
        <span>${calculateTotal().toFixed(2)}</span>
      </div>

      <div className="order-actions">
        <button onClick={handleBack} className="back-button">
          Back to Menu
        </button>
        <button className="confirm-button">
          Confirm Order
        </button>
      </div>
    </div>
  );
}

export default OrderDetails;
